import React from "react";

import "./App.css";

class App extends React.Component {
  state = {
    user:{},
    active: false,
  }

  handleToggle = () => {
    console.log("handleToggle");
    fetch('https://api.github.com/users/a-github-username').then
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.handleToggle}>Toggle</button>
      </div>
    );
  }
}
export default App;
