import "./App.css";

class App extends React.Component {
  state = {
    user:{},
    active: false,
  }

  handleToggle = event => {
    console.log("handleToggle");
    fetch('https://api.github.com/users/kramoliya')
    .then(res=>res.json())
    .then(user=>{
      this.setState({ user })
    });
  };

  img

  render() {
    return (
      <React.Fragment>
        <button onClick={this.handleToggle}>Toggle</button>
        <div><img /><h1>username</h1><p>bio</p><p>id</p></div>;
        </React.Fragment>
    );
  }
}
export default App;
